    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed
        if(txtNombre.getText().equals("")||txtApellido.getText().equals("")||
                txtUsuario.getText().equals("")||txtPass.getText().equals("")|| cmbRol.getSelectedItem() == null){
            mostrarMensaje("Todos los campos deben ser completados para crear el registro", "Error", "Campos vacios");
        }else{
            ctrlLogica.guardarUsuario(txtNombre.getText(), txtApellido.getText(), txtUsuario.getText(), txtPass.getText(),
                (String)cmbRol.getSelectedItem());
            mostrarMensaje("El registro ha sido creado correctamente", "info", "Guardado exitoso");
        }

    }//GEN-LAST:event_btnGuardarActionPerformed

    private void btnAltaMasivaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAltaMasivaActionPerformed
        JFileChooser fileChooser = new JFileChooser();
        FileNameExtensionFilter filter = new FileNameExtensionFilter("Archivos de texto (*.txt)", "txt");
        fileChooser.setFileFilter(filter);
        int seleccion = fileChooser.showOpenDialog(null);
        if (seleccion == JFileChooser.APPROVE_OPTION) {
            java.io.File archivoSeleccionado = fileChooser.getSelectedFile();
            String rutaArchivo = archivoSeleccionado.getAbsolutePath();
            ctrlLogica.altaMasiva(rutaArchivo);
            this.dispose();
        } else {
            mostrarMensaje("La seleccion de archivo ha sido cancelada", "Info", "Seleccion cancelada");
        }
    }//GEN-LAST:event_btnAltaMasivaActionPerformed

